mapper.select:
SELECT result1.* FROM (SELECT num,writer,title,content,viewCount,isSecret,reservetype, TO_CHAR(regdate,'YY.MM.DD HH24:MI') AS regdate, LAG(num,1,0) OVER (ORDER BY num DESC) AS prevNum, LEAD(num,1,0) OVER (ORDER BY num DESC) AS nextNum FROM board_cs
WHERE title LIKE '%'||#{title}||'%' OR content LIKE '%'||#{content}||'%'
WHERE title LIKE '%'||#{title}||'%'
WHERE writer LIKE '%'||#{writer}||'%'
) result1 WHERE num=#{num}

mapper.update:
UPDATE board_cs
SET viewCount=viewCount+1
WHERE num=#{num}

UPDATE board_cs
SET title=#{title}, content=#{content}, isSecret=#{isSecret}, reservetype=#{reservetype}
WHERE num=#{num}
